import React from 'react';

function Navigation({ currentPage, onPageChange }) {
  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      gap: '20px',
      marginBottom: '30px',
      padding: '20px'
    }}>
      <button
        onClick={() => onPageChange('sorting')}
        style={{
          padding: '12px 24px',
          backgroundColor: currentPage === 'sorting' ? '#06923E' : '#444',
          color: 'white',
          border: 'none',
          borderRadius: '25px',
          cursor: 'pointer',
          fontSize: '16px',
          fontWeight: '500',
          transition: 'all 0.3s ease',
          boxShadow: currentPage === 'sorting' ? '0 4px 15px rgba(6, 146, 62, 0.3)' : 'none'
        }}
      >
        Sorting Algorithms
      </button>
      <button
        onClick={() => onPageChange('searching')}
        style={{
          padding: '12px 24px',
          backgroundColor: currentPage === 'searching' ? '#06923E' : '#444',
          color: 'white',
          border: 'none',
          borderRadius: '25px',
          cursor: 'pointer',
          fontSize: '16px',
          fontWeight: '500',
          transition: 'all 0.3s ease',
          boxShadow: currentPage === 'searching' ? '0 4px 15px rgba(6, 146, 62, 0.3)' : 'none'
        }}
      >
        Search Algorithms
      </button>
    </div>
  );
}

export default Navigation;
