import React, { useState, useEffect, useCallback, useRef } from 'react';
import Navigation from './Navigation';
import SearchVisualizer from './SearchVisualizer';

const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

function App() {
  const [currentPage, setCurrentPage] = useState('sorting');
  const [array, setArray] = useState([]);
  const [numBars, setNumBars] = useState(15);
  const [isSorting, setIsSorting] = useState(false);
  const [barStates, setBarStates] = useState({});
  
  // Use useRef to track stop state for immediate access
  const isStopRequested = useRef(false);

  // Generate random array
  const generateArray = useCallback(() => {
    const newArray = [];
    for (let i = 0; i < numBars; i++) {
      newArray.push(Math.floor(Math.random() * 200) + 20);
    }
    setArray(newArray);
    setBarStates({});
  }, [numBars]);

  // Initialize array on component mount
  useEffect(() => {
    generateArray();
  }, [generateArray]);

  // Update bar count
  const updateBarCount = (value) => {
    setNumBars(parseInt(value));
  };

  // Stop sorting
  const stopSorting = () => {
    isStopRequested.current = true;
    setIsSorting(false);
  };

  // Reset visualization
  const resetVisualization = () => {
    isStopRequested.current = true;
    setIsSorting(false);
    setBarStates({});
    setTimeout(() => {
      generateArray();
      isStopRequested.current = false;
    }, 100);
  };

  // Bubble Sort
  const bubbleSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    const newBarStates = {};

    for (let i = 0; i < newArray.length - 1 && !isStopRequested.current; i++) {
      for (let j = 0; j < newArray.length - i - 1 && !isStopRequested.current; j++) {
        newBarStates[j] = 'comparing';
        newBarStates[j + 1] = 'comparing';
        setBarStates({ ...newBarStates });
        
        await sleep(100);
        
        if (isStopRequested.current) break;

        if (newArray[j] > newArray[j + 1]) {
          [newArray[j], newArray[j + 1]] = [newArray[j + 1], newArray[j]];
          setArray([...newArray]);
        }

        newBarStates[j] = '';
        newBarStates[j + 1] = '';
        setBarStates({ ...newBarStates });
      }
      if (!isStopRequested.current) {
        newBarStates[newArray.length - 1 - i] = 'sorted';
        setBarStates({ ...newBarStates });
      }
    }

    if (!isStopRequested.current) {
      newBarStates[0] = 'sorted';
      setBarStates({ ...newBarStates });
    }
    
    setIsSorting(false);
  };

  // Insertion Sort
  const insertionSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    const newBarStates = {};

    for (let i = 1; i < newArray.length && !isStopRequested.current; i++) {
      let key = newArray[i];
      let j = i - 1;

      newBarStates[i] = 'current';
      setBarStates({ ...newBarStates });
      await sleep(150);
      
      if (isStopRequested.current) break;

      while (j >= 0 && newArray[j] > key && !isStopRequested.current) {
        newBarStates[j] = 'comparing';
        setBarStates({ ...newBarStates });
        
        newArray[j + 1] = newArray[j];
        setArray([...newArray]);
        await sleep(100);
        
        if (isStopRequested.current) break;
        
        newBarStates[j] = '';
        setBarStates({ ...newBarStates });
        j--;
      }

      if (!isStopRequested.current) {
        newArray[j + 1] = key;
        setArray([...newArray]);
        newBarStates[i] = '';
        setBarStates({ ...newBarStates });
      }
    }

    if (!isStopRequested.current) {
      for (let k = 0; k < newArray.length; k++) {
        newBarStates[k] = 'sorted';
      }
      setBarStates({ ...newBarStates });
    }
    
    setIsSorting(false);
  };

  // Selection Sort
  const selectionSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    const newBarStates = {};

    for (let i = 0; i < newArray.length && !isStopRequested.current; i++) {
      let minIdx = i;
      newBarStates[minIdx] = 'current';
      setBarStates({ ...newBarStates });

      for (let j = i + 1; j < newArray.length && !isStopRequested.current; j++) {
        newBarStates[j] = 'comparing';
        setBarStates({ ...newBarStates });
        await sleep(100);
        
        if (isStopRequested.current) break;

        if (newArray[j] < newArray[minIdx]) {
          newBarStates[minIdx] = '';
          minIdx = j;
          newBarStates[minIdx] = 'current';
          setBarStates({ ...newBarStates });
        } else {
          newBarStates[j] = '';
          setBarStates({ ...newBarStates });
        }
      }

      if (!isStopRequested.current && minIdx !== i) {
        [newArray[i], newArray[minIdx]] = [newArray[minIdx], newArray[i]];
        setArray([...newArray]);
      }

      if (!isStopRequested.current) {
        newBarStates[i] = 'sorted';
        newBarStates[minIdx] = '';
        setBarStates({ ...newBarStates });
      }
    }
    
    setIsSorting(false);
  };

  // Quick Sort
  const quickSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    await quickSortHelper(newArray, 0, newArray.length - 1);
    
    if (!isStopRequested.current) {
      const newBarStates = {};
      for (let i = 0; i < newArray.length; i++) {
        newBarStates[i] = 'sorted';
      }
      setBarStates({ ...newBarStates });
    }
    
    setIsSorting(false);
  };

  const quickSortHelper = async (arr, low, high) => {
    if (isStopRequested.current) return;
    
    if (low < high && !isStopRequested.current) {
      let pi = await partition(arr, low, high);
      await quickSortHelper(arr, low, pi - 1);
      await quickSortHelper(arr, pi + 1, high);
    }
  };

  const partition = async (arr, low, high) => {
    if (isStopRequested.current) return low;
    
    let pivot = arr[high];
    let i = low - 1;

    for (let j = low; j < high && !isStopRequested.current; j++) {
      setBarStates(prev => ({ ...prev, [j]: 'comparing', [high]: 'current' }));
      await sleep(150);
      
      if (isStopRequested.current) break;

      if (arr[j] < pivot) {
        i++;
        [arr[i], arr[j]] = [arr[j], arr[i]];
        setArray([...arr]);
      }

      setBarStates(prev => ({ ...prev, [j]: '' }));
    }

    if (!isStopRequested.current) {
      [arr[i + 1], arr[high]] = [arr[high], arr[i + 1]];
      setArray([...arr]);
      setBarStates(prev => ({ ...prev, [high]: '' }));
    }

    return i + 1;
  };

  // Merge Sort
  const mergeSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    await mergeSortHelper(newArray, 0, newArray.length - 1);
    
    if (!isStopRequested.current) {
      const newBarStates = {};
      for (let i = 0; i < newArray.length; i++) {
        newBarStates[i] = 'sorted';
      }
      setBarStates({ ...newBarStates });
    }
    
    setIsSorting(false);
  };

  const mergeSortHelper = async (arr, start, end) => {
    if (isStopRequested.current) return;
    
    if (start < end && !isStopRequested.current) {
      let mid = Math.floor((start + end) / 2);
      await mergeSortHelper(arr, start, mid);
      await mergeSortHelper(arr, mid + 1, end);
      await merge(arr, start, mid, end);
    }
  };

  const merge = async (arr, start, mid, end) => {
    if (isStopRequested.current) return;
    
    let left = arr.slice(start, mid + 1);
    let right = arr.slice(mid + 1, end + 1);

    let i = 0, j = 0, k = start;

    while (i < left.length && j < right.length && !isStopRequested.current) {
      setBarStates(prev => ({ ...prev, [k]: 'comparing' }));
      await sleep(150);
      
      if (isStopRequested.current) break;

      if (left[i] <= right[j]) {
        arr[k] = left[i++];
      } else {
        arr[k] = right[j++];
      }

      setArray([...arr]);
      setBarStates(prev => ({ ...prev, [k]: '' }));
      k++;
    }

    while (i < left.length && !isStopRequested.current) {
      arr[k] = left[i++];
      setArray([...arr]);
      k++;
      await sleep(100);
    }

    while (j < right.length && !isStopRequested.current) {
      arr[k] = right[j++];
      setArray([...arr]);
      k++;
      await sleep(100);
    }
  };

  // Heap Sort
  const heapSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    let n = newArray.length;

    // Build heap
    for (let i = Math.floor(n / 2) - 1; i >= 0 && !isStopRequested.current; i--) {
      await heapify(newArray, n, i);
    }

    // Extract elements from heap
    for (let i = n - 1; i > 0 && !isStopRequested.current; i--) {
      [newArray[0], newArray[i]] = [newArray[i], newArray[0]];
      setArray([...newArray]);

      if (!isStopRequested.current) {
        setBarStates(prev => ({ ...prev, [i]: 'sorted' }));
        await heapify(newArray, i, 0);
      }
    }

    if (!isStopRequested.current) {
      setBarStates(prev => ({ ...prev, [0]: 'sorted' }));
    }
    
    setIsSorting(false);
  };

  const heapify = async (arr, n, i) => {
    if (isStopRequested.current) return;
    
    let largest = i;
    let l = 2 * i + 1;
    let r = 2 * i + 2;

    if (l < n && arr[l] > arr[largest]) largest = l;
    if (r < n && arr[r] > arr[largest]) largest = r;

    if (largest !== i && !isStopRequested.current) {
      [arr[i], arr[largest]] = [arr[largest], arr[i]];
      setArray([...arr]);
      
      setBarStates(prev => ({ 
        ...prev, 
        [i]: 'comparing', 
        [largest]: 'comparing' 
      }));

      await sleep(150);
      
      if (!isStopRequested.current) {
        setBarStates(prev => ({ 
          ...prev, 
          [i]: '', 
          [largest]: '' 
        }));
        await heapify(arr, n, largest);
      }
    }
  };

  // Shell Sort
  const shellSort = async () => {
    if (isSorting) return;
    setIsSorting(true);
    isStopRequested.current = false;

    const newArray = [...array];
    let n = newArray.length;
    let gap = Math.floor(n / 2);

    while (gap > 0 && !isStopRequested.current) {
      for (let i = gap; i < n && !isStopRequested.current; i++) {
        let temp = newArray[i];
        let j = i;

        setBarStates(prev => ({ ...prev, [i]: 'current' }));
        await sleep(150);

        while (j >= gap && newArray[j - gap] > temp && !isStopRequested.current) {
          setBarStates(prev => ({ ...prev, [j - gap]: 'comparing' }));
          newArray[j] = newArray[j - gap];
          setArray([...newArray]);
          await sleep(100);
          setBarStates(prev => ({ ...prev, [j - gap]: '' }));
          j -= gap;
        }

        if (!isStopRequested.current) {
          newArray[j] = temp;
          setArray([...newArray]);
          setBarStates(prev => ({ ...prev, [i]: '' }));
        }
      }
      gap = Math.floor(gap / 2);
    }

    if (!isStopRequested.current) {
      const newBarStates = {};
      for (let k = 0; k < newArray.length; k++) {
        newBarStates[k] = 'sorted';
      }
      setBarStates({ ...newBarStates });
    }

    setIsSorting(false);
  };

  // Start sorting based on algorithm
  const startSorting = async (algorithm) => {
    if (isSorting) {
      alert("Sorting is already in progress!");
      return;
    }
    
    if (!array || array.length === 0) {
      alert("Please generate an array first!");
      return;
    }
    
    switch (algorithm) {
      case "bubble":
        await bubbleSort();
        break;
      case "insertion":
        await insertionSort();
        break;
      case "selection":
        await selectionSort();
        break;
      case "quick":
        await quickSort();
        break;
      case "merge":
        await mergeSort();
        break;
      case "heap":
        await heapSort();
        break;
      case "shell":
        await shellSort();
        break;
      default:
        break;
    }
  };


  return (
    <div style={{ minHeight: '100vh' }}>
      <nav className="navbar">
        <div className="nav-center">AlgoWorks</div>
      </nav>

      <div style={{ padding: '20px' }}>
        <h1>Sorting Algorithm Visualiser</h1>

        <div className="controls">
          <label htmlFor="barCount">Bars:</label>
          <input 
            type="range" 
            id="barCount" 
            min="5" 
            max="50" 
            value={numBars} 
            onChange={(e) => updateBarCount(e.target.value)}
            disabled={isSorting}
          />
          <span>{numBars}</span>
          <br /><br />

          <button 
            onClick={() => startSorting('quick')} 
            disabled={isSorting}
          >
            Quick Sort
          </button>
          <button 
            onClick={() => startSorting('bubble')} 
            disabled={isSorting}
          >
            Bubble Sort
          </button>
          <button 
            onClick={() => startSorting('insertion')} 
            disabled={isSorting}
          >
            Insertion Sort
          </button>
          <button 
            onClick={() => startSorting('selection')} 
            disabled={isSorting}
          >
            Selection Sort
          </button>
          <button 
            onClick={() => startSorting('heap')} 
            disabled={isSorting}
          >
            Heap Sort
          </button>
          <button 
            onClick={() => startSorting('merge')} 
            disabled={isSorting}
          >
            Merge Sort
          </button>
          <button 
            onClick={() => startSorting('shell')} 
            disabled={isSorting}
          >
            Shell Sort
          </button>
        </div>

        <div className="bar-container">
          {array.map((value, index) => (
            <div
              key={index}
              className={`bar ${barStates[index] || ''}`}
              style={{ height: `${value}px` }}
            >
              {value}
            </div>
          ))}
        </div>

        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button className="Gen" onClick={generateArray} disabled={isSorting}>
            Generate Array
          </button>
          <button className="Gen" onClick={stopSorting} disabled={!isSorting}>
            Stop Sorting
          </button>
          <button className="Gen" onClick={resetVisualization}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
