import React, { useState, useEffect, useCallback, useRef } from 'react';

const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

function SearchVisualizer() {
  const [array, setArray] = useState([]);
  const [numBars, setNumBars] = useState(15);
  const [searchValue, setSearchValue] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [barStates, setBarStates] = useState({});
  const [result, setResult] = useState(null);
  
  const isStopRequested = useRef(false);

  // Generate random array
  const generateArray = useCallback(() => {
    const newArray = [];
    for (let i = 0; i < numBars; i++) {
      newArray.push(Math.floor(Math.random() * 100) + 1);
    }
    // Sort array for binary search
    newArray.sort((a, b) => a - b);
    setArray(newArray);
    setBarStates({});
    setResult(null);
  }, [numBars]);

  // Initialize array on component mount
  useEffect(() => {
    generateArray();
  }, [generateArray]);

  // Update bar count
  const updateBarCount = (value) => {
    setNumBars(parseInt(value));
  };

  // Stop searching
  const stopSearching = () => {
    isStopRequested.current = true;
    setIsSearching(false);
  };

  // Reset visualization
  const resetVisualization = () => {
    isStopRequested.current = true;
    setIsSearching(false);
    setBarStates({});
    setResult(null);
    setTimeout(() => {
      generateArray();
      isStopRequested.current = false;
    }, 100);
  };

  // Linear Search
  const linearSearch = async () => {
    if (isSearching) return;
    if (!searchValue || searchValue === '') {
      alert("Please enter a value to search!");
      return;
    }

    setIsSearching(true);
    isStopRequested.current = false;
    setResult(null);

    const searchVal = parseInt(searchValue);
    const newBarStates = {};

    for (let i = 0; i < array.length && !isStopRequested.current; i++) {
      newBarStates[i] = 'searching';
      setBarStates({ ...newBarStates });
      
      await sleep(300);
      
      if (isStopRequested.current) break;

      if (array[i] === searchVal) {
        newBarStates[i] = 'found';
        setBarStates({ ...newBarStates });
        setResult(`Found ${searchVal} at index ${i}`);
        setIsSearching(false);
        return;
      } else {
        newBarStates[i] = 'searched';
        setBarStates({ ...newBarStates });
      }
    }

    if (!isStopRequested.current) {
      setResult(`${searchVal} not found in the array`);
    }
    
    setIsSearching(false);
  };

  // Binary Search
  const binarySearch = async () => {
    if (isSearching) return;
    if (!searchValue || searchValue === '') {
      alert("Please enter a value to search!");
      return;
    }

    setIsSearching(true);
    isStopRequested.current = false;
    setResult(null);

    const searchVal = parseInt(searchValue);
    const newBarStates = {};
    let left = 0;
    let right = array.length - 1;

    while (left <= right && !isStopRequested.current) {
      const mid = Math.floor((left + right) / 2);
      
      // Highlight current search area
      for (let i = left; i <= right; i++) {
        newBarStates[i] = 'searching';
      }
      newBarStates[mid] = 'current';
      setBarStates({ ...newBarStates });
      
      await sleep(400);
      
      if (isStopRequested.current) break;

      if (array[mid] === searchVal) {
        newBarStates[mid] = 'found';
        setBarStates({ ...newBarStates });
        setResult(`Found ${searchVal} at index ${mid}`);
        setIsSearching(false);
        return;
      } else if (array[mid] < searchVal) {
        // Mark left half as searched
        for (let i = left; i <= mid; i++) {
          newBarStates[i] = 'searched';
        }
        left = mid + 1;
      } else {
        // Mark right half as searched
        for (let i = mid; i <= right; i++) {
          newBarStates[i] = 'searched';
        }
        right = mid - 1;
      }
      
      setBarStates({ ...newBarStates });
      await sleep(200);
    }

    if (!isStopRequested.current) {
      setResult(`${searchVal} not found in the array`);
    }
    
    setIsSearching(false);
  };

  // Start searching based on algorithm
  const startSearching = async (algorithm) => {
    if (isSearching) {
      alert("Searching is already in progress!");
      return;
    }
    
    if (!array || array.length === 0) {
      alert("Please generate an array first!");
      return;
    }
    
    switch (algorithm) {
      case "linear":
        await linearSearch();
        break;
      case "binary":
        await binarySearch();
        break;
      default:
        break;
    }
  };

  return (
    <div style={{ minHeight: '100vh' }}>
      <nav className="navbar">
        <div className="nav-center">Search Visualizer</div>
      </nav>

      <div style={{ padding: '20px' }}>
        <h1>Searching Algorithm Visualiser</h1>

        <div className="controls">
          <label htmlFor="barCount">Bars:</label>
          <input 
            type="range" 
            id="barCount" 
            min="5" 
            max="30" 
            value={numBars} 
            onChange={(e) => updateBarCount(e.target.value)}
            disabled={isSearching}
          />
          <span>{numBars}</span>
          <br /><br />

          <label htmlFor="searchValue">Search Value:</label>
          <input 
            type="number" 
            id="searchValue"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
            placeholder="Enter value to search"
            disabled={isSearching}
            style={{
              padding: '8px 12px',
              margin: '0 10px',
              borderRadius: '6px',
              border: '1px solid #444',
              backgroundColor: '#2c2c2c',
              color: '#D3ECCD',
              fontSize: '14px'
            }}
          />
          <br /><br />

          <button 
            onClick={() => startSearching('linear')} 
            disabled={isSearching}
          >
            Linear Search
          </button>
          <button 
            onClick={() => startSearching('binary')} 
            disabled={isSearching}
          >
            Binary Search
          </button>
        </div>

        {result && (
          <div style={{ 
            textAlign: 'center', 
            margin: '20px 0',
            padding: '15px',
            backgroundColor: result.includes('Found') ? '#06923E' : '#E67514',
            borderRadius: '8px',
            color: 'white',
            fontWeight: 'bold'
          }}>
            {result}
          </div>
        )}

        <div className="bar-container">
          {array.map((value, index) => (
            <div
              key={index}
              className={`bar ${barStates[index] || ''}`}
              style={{ height: `${value * 2}px` }}
            >
              {value}
            </div>
          ))}
        </div>

        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button className="Gen" onClick={generateArray} disabled={isSearching}>
            Generate Array
          </button>
          <button className="Gen" onClick={stopSearching} disabled={!isSearching}>
            Stop Searching
          </button>
          <button className="Gen" onClick={resetVisualization}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
}

export default SearchVisualizer;
